import React, { useEffect, useState } from 'react';
import api from '../api/client.js';

export default function AdminBookings(){
  const [bookings, setBookings] = useState([]);
  useEffect(()=>{ (async()=>{ const {data} = await api.get('/bookings'); setBookings(data); })(); },[]);

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Bookings</h1>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white rounded-2xl overflow-hidden">
          <thead className="bg-gray-100">
            <tr>
              <th className="p-2 text-left">Customer</th>
              <th className="p-2 text-left">Email</th>
              <th className="p-2 text-left">Contact</th>
              <th className="p-2 text-left">Tour</th>
              <th className="p-2 text-left">When</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map(b => (
              <tr key={b._id} className="border-t">
                <td className="p-2">{b.customerName}</td>
                <td className="p-2">{b.userEmail}</td>
                <td className="p-2">{b.contactNumber}</td>
                <td className="p-2">{b.tour?.name}</td>
                <td className="p-2">{new Date(b.createdAt).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
