import React from 'react';
import { Link, NavLink } from 'react-router-dom';
import { useAuth } from '../context/AuthContext.jsx';

export default function Navbar() {
  const { user, logout } = useAuth();

  return (
    <nav className="bg-white shadow sticky top-0 z-50">
      <div className="max-w-6xl mx-auto px-4 py-3 flex items-center justify-between">
        <Link to="/" className="text-xl font-bold">TourGuide</Link>
        <div className="flex items-center gap-4">
          <NavLink to="/tours" className={({isActive})=> isActive? 'font-semibold' : ''}>Tours</NavLink>
          <NavLink to="/contact" className={({isActive})=> isActive? 'font-semibold' : ''}>Contact</NavLink>
          <NavLink to="/about" className={({isActive})=> isActive? 'font-semibold' : ''}>About</NavLink>
          {user?.role === 'admin' && (
            <>
              <NavLink to="/admin/tours">Admin Tours</NavLink>
              <NavLink to="/admin/bookings">Bookings</NavLink>
              <NavLink to="/admin/media">Home Media</NavLink>
            </>
          )}
          {!user ? (
            <>
              <NavLink to="/login" className="btn btn-outline">Login</NavLink>
              <NavLink to="/register" className="btn btn-primary">Register</NavLink>
            </>
          ) : (
            <button onClick={logout} className="btn btn-outline">Logout</button>
          )}
        </div>
      </div>
    </nav>
  );
}
