import React, { useEffect, useState } from 'react';
import api from '../api/client.js';
import { Link } from 'react-router-dom';

export default function Tours(){
  const [tours, setTours] = useState([]);

  useEffect(()=>{ (async()=>{
    const { data } = await api.get('/tours');
    setTours(data);
  })(); },[]);

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Available Tours</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {tours.map(t=> (
          <Link key={t._id} to={`/tours/${t._id}`} className="card hover:shadow-lg">
            {t.photos?.[0]?.url && <img src={t.photos[0].url} className="w-full h-48 object-cover rounded-xl mb-3"/>}
            <div className="flex justify-between items-center">
              <div>
                <h3 className="font-semibold">{t.name}</h3>
                <p className="text-sm text-gray-500">{t.days} days â€¢ Rs {t.price}</p>
              </div>
              <span className="text-blue-600 font-semibold">View</span>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
