import React, { useEffect, useState } from 'react';
import api from '../api/client.js';
import { Link } from 'react-router-dom';

export default function Home(){
  const [media, setMedia] = useState([]);
  const [tours, setTours] = useState([]);

  useEffect(()=>{ (async()=>{
    const m = await api.get('/media');
    setMedia(m.data);
    const t = await api.get('/tours');
    setTours(t.data.slice(0,3));
  })(); },[]);

  return (
    <div className="space-y-8">
      <section className="relative h-64 md:h-96 rounded-2xl overflow-hidden">
        {media.find(x=>x.type==='video') ? (
          <video src={media.find(x=>x.type==='video').url} className="w-full h-full object-cover" autoPlay muted loop />
        ) : (
          <img src={media[0]?.url} className="w-full h-full object-cover" />
        )}
        <div className="absolute inset-0 bg-black/30 flex items-center justify-center">
          <h1 className="text-3xl md:text-5xl text-white font-bold">Discover Sri Lanka</h1>
        </div>
      </section>

      <section>
        <h2 className="text-2xl font-bold mb-4">Featured Tours</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {tours.map(t => (
            <Link key={t._id} to={`/tours/${t._id}`} className="card hover:shadow-lg">
              {t.photos?.[0]?.url && <img src={t.photos[0].url} className="w-full h-48 object-cover rounded-xl mb-3"/>}
              <div className="flex justify-between">
                <div>
                  <h3 className="font-semibold">{t.name}</h3>
                  <p className="text-sm text-gray-500">{t.days} days â€¢ Rs {t.price}</p>
                </div>
                <span className="text-blue-600 font-semibold">Book</span>
              </div>
            </Link>
          ))}
        </div>
      </section>
    </div>
  );
}
