import React, { useEffect, useState } from 'react';
import api from '../api/client.js';

export default function AdminTours(){
  const [tours, setTours] = useState([]);
  const [form, setForm] = useState({ name:'', price:'', contactNumber:'', places:'', days:'', description:'' });
  const [files, setFiles] = useState([]);

  const load = async()=>{ const { data } = await api.get('/tours'); setTours(data); };
  useEffect(()=>{ load(); },[]);

  const onCreate = async(e)=>{
    e.preventDefault();
    const fd = new FormData();
    Object.entries(form).forEach(([k,v])=> fd.append(k, v));
    Array.from(files).forEach(f=> fd.append('photos', f));
    await api.post('/tours', fd, { headers: { 'Content-Type': 'multipart/form-data' }});
    setForm({ name:'', price:'', contactNumber:'', places:'', days:'', description:'' });
    setFiles([]);
    await load();
  };

  const onDelete = async(id)=>{ await api.delete(`/tours/${id}`); await load(); };

  return (
    <div className="space-y-6">
      <h1 className="text-2xl font-bold">Manage Tours</h1>
      <form onSubmit={onCreate} className="card grid md:grid-cols-2 gap-3">
        <input className="input" placeholder="Name" value={form.name} onChange={e=>setForm({...form, name:e.target.value})} />
        <input className="input" placeholder="Price" type="number" value={form.price} onChange={e=>setForm({...form, price:e.target.value})} />
        <input className="input" placeholder="Contact number" value={form.contactNumber} onChange={e=>setForm({...form, contactNumber:e.target.value})} />
        <input className="input" placeholder="Places (comma-separated)" value={form.places} onChange={e=>setForm({...form, places:e.target.value})} />
        <input className="input" placeholder="Days" type="number" value={form.days} onChange={e=>setForm({...form, days:e.target.value})} />
        <textarea className="input md:col-span-2" placeholder="Description" value={form.description} onChange={e=>setForm({...form, description:e.target.value})} />
        <input type="file" accept="image/*" multiple onChange={e=>setFiles(e.target.files)} />
        <button className="btn btn-primary">Create</button>
      </form>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {tours.map(t => (
          <div key={t._id} className="card">
            {t.photos?.[0]?.url && <img src={t.photos[0].url} className="w-full h-44 object-cover rounded-xl mb-3"/>}
            <h3 className="font-semibold">{t.name}</h3>
            <p className="text-sm text-gray-500">{t.days} days â€¢ Rs {t.price}</p>
            <button className="btn btn-outline mt-2" onClick={()=>onDelete(t._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}
