import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api/client.js';

export default function TourDetails(){
  const { id } = useParams();
  const [tour, setTour] = useState(null);
  const [booking, setBooking] = useState({ customerName:'', userEmail:'', contactNumber:'', notes:'' });
  const [msg, setMsg] = useState('');

  useEffect(()=>{ (async()=>{
    const { data } = await api.get(`/tours/${id}`);
    setTour(data);
  })(); },[id]);

  const onBook = async(e)=>{
    e.preventDefault(); setMsg('');
    const payload = { ...booking, tourId: id };
    await api.post('/bookings', payload);
    setMsg('Booked! Check your email inbox (record saved).');
    setBooking({ customerName:'', userEmail:'', contactNumber:'', notes:'' });
  };

  if (!tour) return null;
  return (
    <div className="grid lg:grid-cols-2 gap-6">
      <div>
        <div className="grid grid-cols-3 gap-2">
          {tour.photos?.slice(0,5).map((p,idx)=> (
            <img key={idx} src={p.url} className="w-full h-36 object-cover rounded-xl" />
          ))}
        </div>
        <h1 className="text-3xl font-bold mt-4">{tour.name}</h1>
        <p className="text-gray-600 mt-2">{tour.description}</p>
        <p className="mt-2"><b>Places:</b> {tour.places.join(', ')}</p>
        <p className="mt-1"><b>Days:</b> {tour.days} â€¢ <b>Price:</b> Rs {tour.price}</p>
        <p className="mt-1"><b>Contact:</b> {tour.contactNumber}</p>
      </div>
      <div className="card">
        <h2 className="text-xl font-semibold mb-2">Book this tour</h2>
        {msg && <div className="bg-green-100 text-green-700 p-2 rounded mb-2">{msg}</div>}
        <form onSubmit={onBook} className="space-y-3">
          <input className="input" placeholder="Your name" value={booking.customerName} onChange={e=>setBooking({...booking, customerName:e.target.value})} />
          <input className="input" type="email" placeholder="Your email" value={booking.userEmail} onChange={e=>setBooking({...booking, userEmail:e.target.value})} />
          <input className="input" placeholder="Contact number" value={booking.contactNumber} onChange={e=>setBooking({...booking, contactNumber:e.target.value})} />
          <textarea className="input" placeholder="Notes" value={booking.notes} onChange={e=>setBooking({...booking, notes:e.target.value})} />
          <button className="btn btn-primary w-full">Confirm Booking</button>
        </form>
      </div>
    </div>
  );
}
